mapper.update:
update login
set name = #{name,jdbcType=VARCHAR},
  age = #{age,jdbcType=VARCHAR},
  user_name = #{userName,jdbcType=VARCHAR},
  password = #{password,jdbcType=VARCHAR}
where id = #{id,jdbcType=BIGINT}

mapper.select:
select id, name, age, user_name, password
from login
where id = #{id,jdbcType=BIGINT}
